public:
    BSTHashTable(size_t capacity = 10) : capacity(capacity), table(capacity) {}

    void insert(const K& key, const V& value) {
        size_t index = std::hash<K>{}(key) % capacity;
        table[index] = insertNode(table[index], key, value);
    }

